import { Component } from '@angular/core';
import { Tutorial } from 'src/app/models/tutorial.model';
import { TutorialService } from 'src/app/services/tutorial.service';

@Component({
  selector: 'app-add-tutorial',
  templateUrl: './add-tutorial.component.html',
  styleUrls: ['./add-tutorial.component.css'],
})
export class AddTutorialComponent {
  tutorial: Tutorial = {
    title: '',
    description: '',
    published: false
  };
  submitted = false;

  constructor(private tutorialService: TutorialService) {}

  saveTutorial(): void {
    const data = {
      title: this.tutorial.title,
      description: this.tutorial.description
    };

    this.tutorialService.create(data).subscribe({
      next: (res) => {
        console.log(res);
        this.submitted = true;
      },
      error: (e) => console.error(e)
    });
  }

  newTutorial(): void {
    this.submitted = false;
    this.tutorial = {
      title: '',
      description: '',
      published: false
    };
  }
}
<div class="container my-5">
  <div class="row justify-content-center">
    <div class="col-md-8">
      <div class="card shadow-sm">
        <div class="card-header bg-primary text-white">
          <h4 class="mb-0">Create a New Tutorial</h4>
        </div>
        <div class="card-body">
          <!-- Form for creating a tutorial -->
          <div *ngIf="!submitted">
            <div class="form-group mb-4">
              <label for="title" class="form-label">Title</label>
              <input
                type="text"
                class="form-control"
                id="title"
                required
                [(ngModel)]="tutorial.title"
                name="title"
                placeholder="Enter tutorial title"
              />
            </div>

            <div class="form-group mb-4">
              <label for="description" class="form-label">Description</label>
              <textarea
                class="form-control"
                id="description"
                required
                [(ngModel)]="tutorial.description"
                name="description"
                rows="4"
                placeholder="Enter tutorial description"
              ></textarea>
            </div>

            <button (click)="saveTutorial()" class="btn btn-primary w-100">
              <i class="fas fa-save"></i> Submit
            </button>
          </div>

          <!-- Confirmation after submission -->
          <div *ngIf="submitted" class="text-center">
            <h4 class="text-success my-4">
              <i class="fas fa-check-circle"></i> Tutorial was submitted successfully!
            </h4>
            <button class="btn btn-success" (click)="newTutorial()">
              <i class="fas fa-plus-circle"></i> Add Another Tutorial
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
